import ContactInfo from '@/components/checkout/ContactInfo.vue';
import ShippingInfo from '@/components/checkout/ShippingInfo.vue';
import Review from '@/components/checkout/Review.vue';
export default {
  components: {
    ContactInfo: ContactInfo,
    ShippingInfo: ShippingInfo,
    Review: Review
  },
  data: function data() {
    return {
      step: 1,
      checkoutForm: false,
      data: {
        email: '',
        name: '',
        phone: '',
        street: '',
        state: '',
        zip: ''
      },
      rules: {
        required: function required(value) {
          return !!value || 'Required.';
        },
        zip: function zip(value) {
          return value.length == 5 || 'Must be five characters';
        },
        email: function email(value) {
          var pattern = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
          return pattern.test(value) || 'Invalid e-mail.';
        },
        phone: function phone(value) {
          var pattern = /\d{10}/;
          return pattern.test(value) || 'Invalid phone number.';
        }
      }
    };
  },
  methods: {
    next: function next() {
      this.step += 1;
    },
    previous: function previous() {
      this.step -= 1;
    },
    submitOrder: function submitOrder() {
      this.$router.push({
        name: 'thankyou'
      });
    }
  }
};
